import Modalpassword from '@/components/core/Detalles17.vue';
import { api } from '@/api';
import $ from 'jquery';
export default {
  components: {
    //    toolbar,
    //      Modaldetalles,
    Modalpassword: Modalpassword
  },
  data: function data() {
    return {
      escrito: '',
      info: null,
      rating: 3,
      id: null,
      show: false,
      rowsPerPageItems: [12, 24, 36],
      pagination: {
        rowsPerPage: 12
      },
      items1: ["Aguascalientes", "Baja California", "Baja California Sur", "Chihuahua", "Chiapas", "Campeche", "Ciudad de Mexico", "Coahuila", "Colima", "Durango", "Guerrero", "Guanajuato", "Hidalgo", "Jalisco", "Michoacan", "Estado de Mexico", "Morelos", "Nayarit", "Nuevo Leon", "Oaxaca", "Puebla", "Quintana Roo", "Queretaro", "Sinaloa", "San Luis Potosi", "Sonora", "Tabasco", "Tlaxcala", "Tamaulipas", "Veracruz", "Yucatan", "Zacatecas"],
      items: null,
      errors: [],
      empty: [],
      content1: null,
      content2: null,
      dialog: false,
      sub: null,
      sub2: null,
      rules1: [function (value) {
        return !!value || 'Required.';
      }, function (value) {
        return (value || '').length <= 50 || 'Max 50 characters';
      }, function (value) {
        var pattern = /^([A-ZÁÉÍÓÚ]{1}[a-zñáéíóú]+[\s]*)+$/;
        return pattern.test(value) || 'Nombre(s) invalido(s).';
      }],
      rules2: [function (value) {
        return !!value || 'Required.';
      }, function (value) {
        return (value || '').length <= 50 || 'Max 50 characters';
      }, function (value) {
        var pattern = /^([A-ZÁÉÍÓÚ]{1}[a-zñáéíóú]+[\s]*)+$/;
        return pattern.test(value) || 'Apellido(s) invalido(s).';
      }],
      rules3: [function (value) {
        return !!value || 'Required.';
      }, function (value) {
        return (value || '').length <= 50 || 'Max 50 characters';
      }, function (value) {
        var pattern = /^[a-z0-9_-]{3,51}$/;
        return pattern.test(value) || 'Username invalido.';
      }],
      rules4: [function (value) {
        return !!value || 'Required.';
      }, function (value) {
        return (value || '').length <= 50 || 'Max 50 characters';
      }, function (value) {
        var pattern = /^([a-z0-9_\.-]+)@([\da-z\.-]+)\.([a-z\.]{2,6})$/;
        return pattern.test(value) || 'Email invalido.';
      }],
      rules5: [function (value) {
        return !!value || 'Required.';
      }, function (value) {
        return (value || '').length <= 50 || 'Max 50 characters';
      }, function (value) {
        var pattern = /^([A-ZÁÉÍÓÚ0-9]{1}[a-zñáéíóú0-9]+[\s]*)+$/;
        return pattern.test(value) || 'Calle invalida.';
      }],
      rules6: [function (value) {
        return !!value || 'Required.';
      }, function (value) {
        return (value || '').length <= 100 || 'Max 100 characters';
      }, function (value) {
        var pattern = /^([A-ZÁÉÍÓÚ0-9]{1}[a-zñáéíóú0-9]+[\s]*)+$/;
        return pattern.test(value) || 'Datos invalidos.';
      }],
      rules8: [function (value) {
        return !!value || 'Required.';
      }, function (value) {
        return (value || '').length <= 10 || 'Max 10 characters';
      }, function (value) {
        var pattern = /^[+]*[(]{0,1}[0-9]{1,4}[)]{0,1}[-\s\./0-9]*$/;
        return pattern.test(value) || 'Telefono invalido.';
      }],
      rules9: [function (value) {
        return !!value || 'Required.';
      }, function (value) {
        return (value || '').length <= 100 || 'Max 100 characters';
      }, function (value) {
        var pattern = /^([A-ZÁÉÍÓÚ]{1}[a-zñáéíóú]+[\s]*)+$/;
        return pattern.test(value) || 'Datos invalidos.';
      }],
      rules10: [function (value) {
        return !!value || 'Required.';
      }, function (value) {
        return (value || '').length <= 50 || 'Max 50 characters';
      }, function (value) {
        var pattern = /^[0-9]{5}$/;
        return pattern.test(value) || 'CP invalido.';
      }],
      rules11: [function (value) {
        return !!value || 'Required.';
      }],
      rules12: [function (value) {
        return !!value || 'Required.';
      }, function (value) {
        return (value || '').length <= 5 || 'Max 5 characters';
      }, function (value) {
        var pattern = /^[0-9A-Za-z]*$/;
        return pattern.test(value) || 'Numero interior invalido.';
      }]
    };
  },
  methods: {
    Update: function Update() {
      var resp = $(event.currentTarget).serializeArray(); //       console.log("Respuesta del update");
      //       console.log(resp);

      var fail = false;

      for (var i = 0; i < resp.length; i++) {
        //       console.log(resp[i].value);
        //       console.log(fail);
        if (i != 7) {
          if (resp[i].value == "") fail = true;
        }
      }

      if (fail == true) {
        alert('Necesitas completar todos los campos');
      } else {
        api.post('/ad-usuarios/update-admin', $(event.currentTarget).serializeArray()).then(function (response) {
          // JSON responses are automatically parsed.
          //           this.models = response.data;
          alert('Actualización realizada con éxito');
          window.location.replace("http://refacenet.org:61/admin"); //console.log(this.items);
        }).catch(function (e) {
          //           this.errors.push(e)
          //  console.log("Error");
          console.log(e);
        });
      }
    }
  },
  created: function created() {
    var _this = this;

    //alert(sessionStorage.getItem("dato"))
    this.info = this.$route.params.info; //alert("ejemplo "+this.info)
    //sessionStorage.setItem("content1","display:none")
    //sessionStorage.setItem("content2",null)
    //alert(this.content2)

    if (sessionStorage.getItem("dato") != null) {
      this.escrito = sessionStorage.getItem("dato");
    } else {
      this.escrito = '';
    }

    api.get("/ad-usuarios/all-users/0").then(function (response) {
      // JSON responses are automatically parsed.
      _this.items = response.data;
      console.log("los items son los siguientes:");
      console.log(_this.items);
      console.log("el nombre es el siguiente:");
      console.log(_this.items.datos_personales.nombres);
    }).catch(function (e) {
      _this.errors.push(e); //  console.log("Error");
      //  console.log(e);

    });
  }
};
