import { api } from '@/api';
export default {
  name: 'Frame',
  props: {
    titulo: String,
    status: String
  },
  data: function data() {
    return {
      cant: 0,
      info: null,
      dialog: false
    };
  },
  methods: {
    eliminar: function eliminar(titulo) {
      api.post('/noticias/delete-noticia/' + titulo).then(function (response) {
        // JSON responses are automatically parsed.
        //           this.models = response.data;
        //console.log(this.items);
        alert('Eliminación realizada con éxito');
        window.location.replace("http://refacenet.org:61/noticias");
      }).catch(function (e) {
        //           this.errors.push(e)
        //  console.log("Error");
        console.log(e);
      });
    },
    reactivar: function reactivar(titulo) {
      api.post('/noticias/active-noticia/' + titulo).then(function (response) {
        // JSON responses are automatically parsed.
        //           this.models = response.data;
        //console.log(this.items);
        alert('Activacion realizada con éxito');
        window.location.replace("http://refacenet.org:61/noticias");
      }).catch(function (e) {
        //           this.errors.push(e)
        //  console.log("Error");
        console.log(e);
      });
    },
    validarBoton: function validarBoton(status) {
      if (status == "Activo") return true;else return false;
    }
  }
};
