import {api} from '@/api'
export default {
    name: 'Frame',
    props: {
      id_promo: String,
      nombre: String,
      status: String
    },
    data () {
      return {
        cant: 0,
        info:null,
        dialog: false,
      }
    },
    methods: {
       eliminar(id_promo){
         api.post('/promociones/delete-promocion/'+id_promo)
         .then(response => {
           // JSON responses are automatically parsed.
//           this.models = response.data;
           console.log(response.data);
           alert('Eliminación realizada con éxito');
           window.location.replace("http://refacenet.org:61/promociones");
         })
         .catch(e => {
//           this.errors.push(e)
         //  console.log("Error");
           console.log(e);
         })
       },
       reactivar(id_promo){
         api.post('/promociones/active-promocion/'+id_promo)
         .then(response => {
           // JSON responses are automatically parsed.
//           this.models = response.data;
           //console.log(this.items);
           alert('Activacion realizada con éxito');
           window.location.replace("http://refacenet.org:61/promociones");
         })
         .catch(e => {
//           this.errors.push(e)
         //  console.log("Error");
           console.log(e);
         })
       },
       validarBoton(status){
          if(status=="Activo")
            return true;
          else
            return false;
       }
    }
}
